import React, { useState } from "react";
import "./AddProduct.css";
import upload_area from "../Assets/upload_area.svg";
import { backend_url } from "../../App";

const AddProduct = () => {
  const [images, setImages] = useState([]); // <-- multiple image state
  const [productDetails, setProductDetails] = useState({
    name: "",
    description: "",
    images: [], // <-- updated field
    category: "women",
    new_price: "",
    old_price: ""
  });

  const AddProduct = async () => {
    const formData = new FormData();
    for (let i = 0; i < images.length; i++) {
      formData.append("product", images[i]);
    }

    let imageUrls = [];

    // Upload images
    await fetch(`${backend_url}/upload`, {
      method: 'POST',
      headers: {
        Accept: 'application/json',
      },
      body: formData,
    })
      .then((resp) => resp.json())
      .then((data) => {
        if (data.success && Array.isArray(data.image_urls)) {
          imageUrls = data.image_urls;
        }
      });

    // Proceed to create product if upload successful
    if (imageUrls.length > 0) {
      const product = {
        ...productDetails,
        images: imageUrls,
      };

      await fetch(`${backend_url}/addproduct`, {
        method: 'POST',
        headers: {
          Accept: 'application/json',
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(product),
      })
        .then((resp) => resp.json())
        .then((data) => {
          data.success ? alert("Product Added") : alert("Failed to Add Product");
        });
    }
  };

  const changeHandler = (e) => {
    setProductDetails({ ...productDetails, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setImages(Array.from(e.target.files));
  };

  return (
    <div className="addproduct">
      <div className="addproduct-itemfield">
        <p>Product title</p>
        <input type="text" name="name" value={productDetails.name} onChange={changeHandler} placeholder="Type here" />
      </div>
      <div className="addproduct-itemfield">
        <p>Product description</p>
        <input type="text" name="description" value={productDetails.description} onChange={changeHandler} placeholder="Type here" />
      </div>
      <div className="addproduct-price">
        <div className="addproduct-itemfield">
          <p>Price</p>
          <input type="number" name="old_price" value={productDetails.old_price} onChange={changeHandler} placeholder="Type here" />
        </div>
        <div className="addproduct-itemfield">
          <p>Offer Price</p>
          <input type="number" name="new_price" value={productDetails.new_price} onChange={changeHandler} placeholder="Type here" />
        </div>
      </div>
      <div className="addproduct-itemfield">
        <p>Product category</p>
        <select value={productDetails.category} name="category" className="add-product-selector" onChange={changeHandler}>
          <option value="women">Women</option>
          <option value="men">Men</option>
          <option value="kid">Kid</option>
        </select>
      </div>
      <div className="addproduct-itemfield">
        <p>Product images (You can upload multiple)</p>
        <label htmlFor="file-input">
          <img
            className="addproduct-thumbnail-img"
            src={images.length === 0 ? upload_area : URL.createObjectURL(images[0])}
            alt=""
          />
        </label>
        <input
          onChange={handleFileChange}
          type="file"
          name="images"
          id="file-input"
          accept="image/*"
          multiple // <-- allow multiple files
          hidden
        />
      </div>
      <button className="addproduct-btn" onClick={AddProduct}>ADD</button>
    </div>
  );
};

export default AddProduct;
